var Group = Class.create();
Group.prototype = (function Group_proto(s) {
	var p = {};
	var TYPE = p.type = 'Group';
	var TABLE = s.TABLE = 'sys_user_group';

	var log = s.log = x_spar2_log.Logger.getLogger(TYPE, gs.getCurrentScopeName());

	RecordWrapper.setup(p, TABLE, 'GM');

	p.initialize = function initialize(grOrSysId) {
		this.set(grOrSysId);
	};

	p.queryGroups = s.queryGroups = function queryGroups(encodedQuery) {
		var groups = [];

		var gr = new GlideRecord(TABLE);
		gr.addEncodedQuery(encodedQuery);
		gr.query();
		while (gr.next()) {
			groups.push(new Group(gr.getUniqueValue()));
		}

		if (log.isDebugging()) {
			log.debug('queryGroups({1}) returns {2}',
					  encodedQuery, RecordWrapper.arrayToString(groups));
		}
		return groups;
	};

	p.getApplicableBoardBuilders = function getApplicableBoardBuilders() {
		var bbgs, bbs, groupSysId = this.getSysId();
		bbgs = BoardBuilderGroup.query((function(gr) {
			gr.addQuery('group', groupSysId);
		}));
		bbs = bbgs.map(function(bbg) {
			return bbg.getBoardBuilder();
		});
		return bbs;
	};

	p.updatedType = function updatedType() {
		log.info('{0} updatedType TODO', this + '');  // TODO
	};

	p.updatedManager = function updatedManager() {
		log.info('{0} updatedManager TODO', this + '');  // TODO
	};

	p.getUserGroups = function getUserGroups() {
		return GroupMember.getUserGroups(this);  // TODO
	};

	p.getUsers = function getUsers() {
		return GroupMember.getUserGroups(user);  // TODO
	};

	return p;
})(Group);  // End Group_proto
