sys_script_include.script:
var Group = Class.create();
Group.prototype = (function Group_proto(s) {
	var p = {};
	var TYPE = p.type = 'Group';
	var TABLE_NAME = s.TABLE_NAME = 'sys_user_group';

	var log = x_spar2_log.Logger.getLogger(TYPE, gs.getCurrentScopeName());

	s.queryGroups = function(encodedQuery) {
		var groups = [];

		var gr = new GlideRecord(TABLE_NAME);
		gr.addEncodedQuery(encodedQuery);
		gr.query();
		while (gr.next()) {
			groups.push(new Group(gr.getUniqueValue()));
		}
		return groups;
	};

	p.initialize = function initialize(grOrSysId) {
		var myGr, mySysId;
		var that = this;

		if (typeof grOrSysId === 'string') {
			mySysId = grOrSysId;
		} else {
			myGr = grOrSysId;
			mySysId = myGr.getUniqueValue();
			that.gr = myGr;
		}

		that.sysId = mySysId;
		that.tableName = TABLE_NAME;

		that.useGr = function useGr(gr) { myGr = gr; };

		function getGr() {
			if (!myGr) {
				myGr = new GlideRecord(TABLE_NAME);
				myGr.get(mySysId);
				that.gr = myGr;
			}
			return myGr;
		}
		that.getGr = getGr;

		that.getSysId = function() { return mySysId; };
		function toString() {
			return '[Group ' + mySysId + (myGr ? myGr.getDisplayValue() : '') + ']';
		} that.toString = toString;

		that.updatedType = function updatedType() {
			log.debug('updatedType');
		};

		that.updatedManager = function updatedManager() {
			log.debug('updatedManager');
		};

		function getUserSysIds() {
			return GroupMember.getUsersGroupSysIds(mySysId);
		} that.getUserSysIds = getUserSysIds;

		that.getUsers = function getUsers() {
			var userSysIds = getGroupSysIds();
			var users = groupSysIds.map(function(id) {
				return new User(id);
			});
		};

	};  // End initialize

	return p;
})(Group);  // End Group_proto
