sys_script_include.script:
Once = function Once() {
	var o_forOwnProperties = Utils.o_forOwnProperties;
	var keys = {};
	var ADDED = 'added ';
	var EXISTS = 'saw ';
	var REMOVED = 'removed ';

	function process(obj, key, newState, objFn) {
		var data, state;

		if (key !== undefined) {
			data = keys[key];

			if (data === undefined) {
				keys[key] = { state: newState, obj: obj };
				if (objFn) {
					objFn(obj);
				}
			} else {
				state = data.state;
				if (state !== ADDED && newState === ADDED) {
					data.state = ADDED;
					data.obj = obj;
					objFn(obj);
				}
			}
		}
	}

	// Register a pre-existing object.
	this.exists = function exists(obj, key) {
		process(obj, key, EXISTS);
	};

	// Add object with no matching key.
	this.add = function add(obj, key, objFn) {
		process(obj, key, ADDED, objFn);
	};

	// Delete objects that exist but were not mentioned by add.
	this.removeRemaining = function removeRemaining(removeFn) {
		o_forOwnProperties(keys, function remove(data, key) {
			if (data.state === EXISTS) {
				data.state = REMOVED;
				removeFn(data.obj);
			}
		});
	};

	this.forEach = function forEach(fn) {
		for (var key in keys) {
			if (keys.hasOwnProperty(key)) {
				var data = keys[key];
				fn(data.obj, key, data.state);
			}
		}
	};

	this.toString = function toString() {
		var buffer = [];
		this.forEach(function(obj, key, state) {
			buffer.push(state);
			buffer.push(key.toString());
			buffer.push(': ');
			buffer.push(obj.toString());
			buffer.push('\n');
		});
		return buffer.join('');
	};

	Once.Description = function Description(key, obj, more) {
		this.key = key;
		this.obj = obj;
		this.more = more;
		this.toString = function() {
			var buffer = ['['];
			buffer.push(key.toString());
			buffer.push(' ');
			buffer.push(obj.toString());
			buffer.push(' (');
			buffer.push(more.toString());
			buffer.push(')]');
			return buffer.join('');
		};
	};
};
Once.prototype = { type: 'Once' };
