sys_script_include.script:
var VtbBoard = Class.create();
VtbBoard.prototype = (function VtbBoard_proto(s) {
	var p = {};
	var TYPE = p.type = 'VtbBoard';
	var TABLE = 'vtb_board';

	var log = s.log = x_spar2_log.Logger.getLogger(TYPE, gs.getCurrentScopeName());

	var rw = RecordWrapper.setup(p, s, TABLE, 'VTB');

	p.initialize = function initialize(grOrSysId, share) {
		rw.set(grOrSysId, share, this);
	};

	p.toString = (function overrideToString(superToString) {
		return function toString() {
			var more;
			if (this.haveGr()) {
				more = this.getGr().getValue('x_maog_vtbb_internal_name');
			}
			return superToString.call(this, more);
		};
	})(p.toString);

	p.rebuildBoard = function rebuildBoard() {
		var vtbGr = this.getGr();
		var group = vtbGr.assignment_group;
		var owner = vtbGr.owner;
		var bbSysId = vtbGr.x_maog_vtbb_builder;
		var bb = new BoardBuilder(bbSysId);

		this.deleteRecord();
		bb.synchVtbRecord(group, owner);
	};

	p.getKey = function getKey() { return s.getKeyFromGr(this.getGr()); };

	s.makeKey = function makeKey(builderGr, groupGr, ownerGr) {
		var buffer = [];
		buffer.push(builderGr);
		buffer.push(groupGr);
		buffer.push(ownerGr);
		return buffer.join(',');
	};

	s.getKeyFromGr = function getKeyFromGr(vtbGr) {
		return s.makeKey(
		vtbGr.getValue('x_maog_vtbb_builder'),
		vtbGr.getValue('assignment_group'),
		vtbGr.getValue('owner'));
	};

	s.initRecord = rw.initRecord;
	s.createRecord = rw.createRecord;
	s.deleteRecord = rw.deleteRecord;
	s.query = rw.query;

	return p;
})(VtbBoard);  // End VtbBoard_proto
