sys_script_include.script:
var BoardBuilderGroup = Class.create();
BoardBuilderGroup.prototype = (function BoardBuilderGroup_proto(s) {
	var p = {};
	var TYPE = p.type = 'BoardBuilderGroup';

	var TABLE_NAME = 'x_maog_vtbb_board_builder_group';

	var log = x_spar2_log.Logger.getLogger(TYPE, gs.getCurrentScopeName());

	// Return array of Groups matching a list of Board Builders,
	// based on the current Board Builder Groups related list.
	s.findGroupsForBoards = function findGroupsForBoards(bbSysIds) {
		var gr, groups = [];

		gr = new GlideRecord(TABLE_NAME);
		gr.addQuery('board_builder', 'IN', bbSysIds);
		gr.query();
		while (gr.next()) {
			groups.push(new BoardBuilderGroup(gr.getUniqueValue()));
		}
		return groups;
	};

	// Add a new link between a board builder and a group.
	s.add = function add(bbSysId, groupSysId) {
		var bbg;
		var gr = new GlideRecord(TABLE_NAME);
		gr.initialize();
		gr.setValue('board_builder', bbSysId);
		gr.setValue('group', groupSysId);
		gr.insert();
		bbg = new BoardBuilderGroup(gr);
		if (log.isDebugging()) log.debug('Added {0}', bbg.toString());
		return bbg;
	};

	function findGroupsForBbs(bbs, groupSysIds) {
		var bbsByTypes;

		// Group board builders by canonical group type clause.
		bbsByTypes = bbs.reduce(function(bbt, bb) {
			var bbtc = bb.getTypes().join();  // BB Type Clause
			if (!bbt[bbtc]) bbt[bbtc] = { bbs: [], groups: []};
			bbt[bbtc].bbs.push(bb);
		}, {});

		// Find groups that match each unique type clause
		(function() {
			var bbTypes, groupGr, cnd;
			var groupSysIdClause = groupSysIds.join();

			for (var bbtc in bbsByTypes) {
				bbTypes = bbtc.split();
				groupGr = new GlideRecord('sys_user_group');
				groupGr.addQuery('sys_id', 'IN', groupSysIdClause);

				for (var i = 0; i < bbTypes.length; i++) {
					groupGr.addQuery('type.sys_id', bbTypes[i]);
				}
				gs.log('findBbsForGroups: {0}', groupGr.getEncodedQuery());

				gr.query();
				while (gr.next()) {
					bbsByTypes[bbtc].groups.push(groupGr.getValue('sys_id'));
				}
			}
		})();
	}


	// Don't save the GlideRecord parameter because it is a cursor.
	p.initialize = function initialize(grOrSysId) {
		var myGr, mySysId, myBoardBuilder, myGroup;
		var that = this;

		if (typeof grOrSysId === 'string') {
			mySysId = grOrSysId;
		} else {
			myGr = grOrSysId;
			mySysId = myGr.getUniqueValue();
			that.gr = myGr;
		}

		that.sysId = mySysId;
		that.tableName = TABLE_NAME;

		that.useGr = function useGr(gr) { myGr = gr; };

		function getGr() {
			if (!myGr) {
				myGr = new GlideRecord(TABLE_NAME);
				myGr.get(mySysId);
				that.gr = myGr;
			}
			return myGr;
		}
		that.getGr = getGr;

		that.getSysId = function() { return mySysId; };

		// Don't instantiate referenced objects just to create the string.
		function toString() {
			var parts = [];
			parts.push('[BBG ');
			parts.push(mySysId);
			parts.push(' ');
			if (myGr) {
				parts.push(myGr.getDisplayValue());
				if (myBoardBuilder) {
					parts.push(myBoardBuilder.toString());
				} else {
					parts.push(BoardBuilder.stringForSysId(myGr.getValue('board_builder')));
				}
				parts.push(' ');
				if (myGroup) {
					parts.push(myGroup.toString());
				} else {
					parts.push(Group.stringForSysId(myGr.getValue('group')));
				}
			}
			parts.push(']');

			return parts.join('');
		} that.toString = toString;

		function getBoardBuilderSysId() {
			return getGr().getValue('board_builder');
		} that.getBoardBuilderSysId = getBoardBuilderSysId;

		function getGroupSysId() {
			return getGr().getValue('group');
		} that.getGroupSysId = getGroupSysId;

		function getBoardBuilder() {
			return new BoardBuilder(getGr().getValue('board_builder'));
		} that.getBoardBuilder = getBoardBuilder;

		function getGroup() {
			return new Group(getGr().getValue('group'));
		} that.getGroup = getGroup;

		that.deleteRecord = function deleteRecord() {
			getGr().deleteRecord();
		};

		// When a record is added, run the board builder
		// for the group's applicable users.
		that.added = function added() {
			var bb, group, userSysIds, filter;

			if (log.isDebugging()) log.debug('{0} added()', toString());

			bb = getBoardBuilder();
			group = getGroup();

			// Find the group's users the builder applies to
			filter = 'userIN' + group.getUserSysIds() + '^' +
				bb.getUserFilter();
			users = Users.queryUsers(filter);
			users = users.filter(function(u) {
				var applies =  u.hasRoleSysIds(bb.getUserRoleSysIds());
				return applies;
			});

			// Find the users with boards

			// Add boards for the remaining users.

			// TODO: Delete boards for users who left the group.
		};

		that.removed = function removed() {
			if (log.isDebugging()) log.debug('{0} removed()', toString());
		};

	};  // End initialize

	return p;
})(BoardBuilderGroup);  // End BoardBuilderGroup_proto
